    public void llenarTabla(){
        DefaultTableModel modelo = (DefaultTableModel) this.tabla.getModel();
        List<MascotaDto> mascotas = new MascotaDAOFile().listarMascotas();
        for(MascotaDto mascota : mascotas){
            String[] fila = {mascota.getNombre(), mascota.getRaza(), String.valueOf(mascota.getEdad()), mascota.getTipo(),
                mascota.getId(), String.valueOf(mascota.getPeso()),mascota.getFechaIngreso(), mascota.getLugarOrigen(), 
                String.valueOf(mascota.getGenero()), String.valueOf(mascota.getPrecio()), mascota.getEstado(), String.valueOf(mascota.getConsultas()), String.valueOf(mascota.getVacunas())};
            modelo.addRow(fila);
        }
    }


