    public void activarTodo(){
        labelId.setEnabled(true);
        id.setEnabled(true);
        labelTipo.setEnabled(true);
        tipo.setEnabled(true);
        labelDescripcion.setEnabled(true);
        descripcion.setEnabled(true);
        labelPrecio.setEnabled(true);
        precio.setEnabled(true);
    }
    
    public void activarSoloDocumento(){
        labelId.setEnabled(true);
        id.setEnabled(true);
        labelTipo.setEnabled(false);
        tipo.setEnabled(false);
        labelDescripcion.setEnabled(false);
        descripcion.setEnabled(false);
        labelPrecio.setEnabled(false);
        precio.setEnabled(false);
    }
