    private void botonAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonAceptarActionPerformed
        boolean exitoso = control.registrarConsultaEnMascota(java.time.LocalDateTime.now().toString(), idMascota.getText().trim(), sintomas.getText().trim(), tratamiento.getText().trim());
        if(exitoso){
            this.dispose();
        }
    }//GEN-LAST:event_botonAceptarActionPerformed

