    private void botonAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonAceptarActionPerformed
        switch(opcion.getSelectedItem().toString()){
            case "Agregar mascota":
                control.almacenarMascota(nombre.getText().trim().toUpperCase(), raza.getText().trim().toUpperCase(), edad.getText().trim(), 
                        tipo.getText().trim().toUpperCase(), id.getText().trim(), peso.getText().trim(), java.time.LocalDate.now().toString(), 
                        lugarOrigen.getText().trim().toUpperCase(), genero.getText().trim().toUpperCase(), precio.getText().trim(), estado.getText().trim().toUpperCase());
                break;    
            case "Consultar mascota":
                control.consultarMascota(id.getText().trim());
                break;
                
            case "Registrar consulta":
                ConsultaGUI consulta = new ConsultaGUI();
                consulta.setVisible(true);
                break;
            
            case "Registrar vacuna":
                VacunaMascotaGUI vacuna = new VacunaMascotaGUI();
                vacuna.setVisible(true);
                break;
                
            case "Actualizar mascota":
                control.actualizarMascota(nombre.getText().trim().toUpperCase(), raza.getText().trim().toUpperCase(), edad.getText().trim(), 
                        tipo.getText().trim().toUpperCase(), id.getText().trim(), peso.getText().trim(), java.time.LocalDate.now().toString(), 
                        lugarOrigen.getText().trim().toUpperCase(), genero.getText().trim().toUpperCase(), precio.getText().trim(), estado.getText().trim().toUpperCase());
                break;
            case "Listar mascotas":
                control.listarMascotas();
                break;
            case "Eliminar mascota":
                control.eliminarMascota(id.getText().trim());
                break;
        }
    }//GEN-LAST:event_botonAceptarActionPerformed

    private void opcionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_opcionActionPerformed
        switch(opcion.getSelectedItem().toString()){
            case "Agregar mascota":
                activarTodo();                
                break;
            case "Consultar mascota":
                activarSoloDocumento();
                break;
            case "Actualizar mascota":
                activarTodo();
                break;
            case "Registrar consulta":
                desactivarTodo();
                break;
            case "Registrar vacuna":
                desactivarTodo();
                break;
            case "Listar mascotas":
                desactivarTodo();
                break;
            case "Eliminar mascota":
                activarSoloDocumento();
                break;
        }
    }//GEN-LAST:event_opcionActionPerformed

